import React, { Component } from 'react';

class Counter extends Component {

    constructor(){
        super();
        this.state = {
            number: 0
        }
    }

    increment(){
        this.setState(()=>{this.state.number+1});
    }

    increment5(){
        this.increment();
        this.increment();
        this.increment();
        this.increment();
        this.increment();
    }

    render() {
        return (
            <div>
                <h4>{this.state.number}</h4>
                <button onClick = { ()=>{ this.increment5() } } >Increment</button>
            </div>
        );
    }
}

export default Counter;
