import React, { Component } from 'react';

class Counter extends Component {

    constructor(){
        super();
        this.state = {
            number: 0
        }
    }

    increment(){
        this.setState({number: number+1});
    }

    render() {
        return (
            <div>
                <h4>{this.state.number}</h4>
                <button>Increment</button>
            </div>
        );
    }
}

export default Counter;
